import React, { Component } from "react";
import axios from "axios";
import "./New.css";
import NewsArticle from "./NewsArticle";
import { Container, Row, Col } from "react-bootstrap";

class New extends Component {
  constructor() {
    super();
    this.state = {
      news: [],
    };
  }

  componentDidMount = () => {
    axios
      .get(
        `http://newsapi.org/v2/top-headlines?country=us&pageSize=9&apiKey=46e14988b345447487b13565cf885182`
      )
      .then((response) => {
        console.log(response.data);
        this.setState({ news: response.data });
      });
  };

  render() {
    return (
      <Container className="New">
        <Row className="Top">
          <p>Top World-News Headlines:</p>
        </Row>
        <Row className="MainNews">
          {
            (this.state.news.map = (New) => (
              <NewsArticle data={New} key={New.url} />
            ))
          }
        </Row>
        <Row className="Lower">
          <Col sm={6} className="A"></Col>
          <Col sm={6} className="B"></Col>
        </Row>
      </Container>
    );
  }
}

export default New;
